mapper.select:
SELECT receipt.id, receipt_p.receipts_type_name fkParentId, receipt.receipts_type_name receiptsTypeName, receipt.is_sys_own isSysOwn, receipt.order_num orderNum, receipt.remark, date_format(receipt.add_time, '%Y-%m-%d %H:%i:%s') addTime, date_format(receipt.update_time, '%Y-%m-%d %H:%i:%s') updateTime from receipts_type_sys receipt left join receipts_type_sys receipt_p on receipt.fk_parent_id = receipt_p.id
and receipt.receipts_type_name like #{receiptsTypeNameCon}
and receipt.fk_parent_id = #{fkParentId}
order by CONVERT( CONCAT_WS('',receipt_p.order_num,receipt.order_num) USING gbk) asc
