mapper.select:
select r.id, date_format(r.refund_date, '%Y-%m-%d') refundDate, r.refund_sum refundSum, l.has_refunded_data hasRefundedData, l.lend_sum lendSum, case when l.has_refunded=1 then '已还清' when l.has_refunded=0 then '未还清' else '未知' end hasRefunded, l.lend_person lendPerson, case when l.lend_or_borrow=1 then '借出' when l.lend_or_borrow=0 then '借入' else '未知' end lendOrBorrow, l.lend_purpose lendPurpose, l.plan_refund_date planRefundDate, l.remark, u.user_name userName, u.login_name loginName, date_format(r.add_time, '%Y-%m-%d %H:%i:%s') addTime, date_format(r.update_time, '%Y-%m-%d %H:%i:%s') updateTime from refund r LEFT JOIN lend l on r.fk_lend_id = l.id left join sys_en_user u on l.fk_user_id = u.id
and r.refund_date >= #{refundDateStart}
and r.refund_date <= #{refundDateEnd}
and u.user_name like #{userNameCon}
and u.login_name = #{loginName}
and l.fk_user_id = #{fkUserId}
order by r.refund_date desc
